sourcesContent:
import cookieParser from "cookie-parser";
import express from "express";
import expressSession from 'express-session';
const router = express.Router();

router.use(cookieParser());
router.use(expressSession({//얘도 템플릿이고, 이렇게 선언해서 사용한다.
    secret: 'dilab',
    resave: true,
    saveUninitialized: true, //기본적으로 왼쪽 세개정도 선언해서 사용
}))

router.get('/logout', (req, res) => { //웹페이지에서 req를 받는다.
    if (req.cookies.user) {  //쿠키가 있으면
        res.clearCookie('user') //쿠키를 초기화 해라
        res.redirect("/"); //로그아웃 됐으니 로그인 화면으로 
    } else {
        res.redirect("/"); //쿠키가 없으면 로그인페이지로 바로
    }
})

module.exports = router;

