// WITHOUT WARRANTIES OR CONDITIONS OF ANY KIND, either express or implied.
// See the License for the specific language governing permissions and
// limitations under the License.

import express from "express";
// sql import
import { insertSql, selectSql } from "../database/sql";

const router = express.Router();

router.get('/', async function (req, res) {
    // class 정보 불러오기
    const classes= await selectSql.getclass();
    const joinedclass=await selectSql.getjoinedclass();
    // console.log(classes)
    if (req.cookies.user) {
        // 불러온 class 정보 같이 넘겨주기
        res.render('select',{ classes,joinedclass, user: req.cookies.user });
    } else {
        res.render('/')
    }

});
router.post('/',async(_req,res)=>{ 
    const vars=_req.body; //신청 버튼을 눌렀을 때 수업 ID (C_ID) 받아오기위함
    const data={
        C_ID:vars.cid,
    };
    await insertSql.insertClass(data); //수업신청 sql문 실행
    console.log(data)
    res.redirect('/sugang');
});

module.exports = router;

